mapper.select:
select p.*,o.* from ParkingInfo p left join CarInfo ca on ca.CarID=p.CarID left join OwnerInfo o on o.OwnerID=ca.OwnerID
AND o.OwnerName LIKE CONCAT('%',#{OwnerName},'%')
AND p.CarID like CONCAT ('%',#{CarID},'%')
AND #{EnterTime1} < a.EnterTime < #{EnterTime2}
AND #{EndTime1} < a.EndTime < #{EndTime2}

select p.*,o.OwnerName from ParkingInfo p left join  CarInfo ca on ca.CarID=p.CarID
                                          left join OwnerInfo o on o.OwnerID=ca.OwnerID
where p.ID=#{ID}
